vec4 Effect2(vec2 uv) {
    // Create flowing color patterns using sine waves and noise
    vec3 baseColor = hsl(
        sin(uv.x * 10.0 + iTime) * 0.5 + 0.5,   // Hue variation along x-axis
        0.7,                                      // Saturation
        0.5                                       // Lightness
    );

    // Add turbulence to create dynamic patterns
    float turb = turbulent(vec3(uv * 2.0, iTime * 0.1));

    // Create flowing river effect using sine waves with time offset
    vec3 color = hsv2rgb(
        vec3(
            sin(uv.x * 5.0 + iTime) * 0.5 + 0.5,   // Hue variation
            0.8,                                    // Saturation
            0.6 + turb * 0.2                        // Lightness with turbulence
        )
    );

    // Add more complex patterns using multiple sine waves
    color += sin(uv.x * 15.0 + iTime * 3.0) * 0.2;
    color += sin(uv.y * 5.0 + iTime * 2.0) * 0.1;

    // Final color adjustment using noise
    float finalNoise = noise(vec2(uv.x * 4.0, uv.y * 2.0 + iTime));
    color *= 1.0 + finalNoise * 0.3;

    return vec4(color, 1.0);
}
