vec4 Effect2(vec2 uv) {
    // Create circular pattern with multiple layers
    float dist = length(uv);
    vec3 color;

    // Layer 1: Base circular pattern
    float layer1 = sin(dist * 8.0 + iTime * 2.0) * 0.5 + 0.5;

    // Layer 2: Adding more layers with different frequencies
    float layer2 = sin(dist * 16.0 + iTime * 3.0) * 0.5 + 0.5;

    // Layer 3: Even more layers for complexity
    float layer3 = sin(dist * 32.0 + iTime * 4.0) * 0.5 + 0.5;

    // Combine layers with noise and turbulence
    vec3 finalColor = hsl(
        (layer1 + layer2 + layer3) * 360.0,  // Hue variation based on layers
        0.7 + turbulent(uv * 4.0) * 0.3,      // Saturation with some noise
        0.5 + sin(iTime) * 0.5               // Brightness modulation over time
    );

    return vec4(hsl2rgb(finalColor), 1.0);
}
