vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 8.0 + iTime;

    // Create flowing pattern using turbulence
    float flow1 = turbulent(st);
    float flow2 = turbulent(st + vec2(1.5, 3.2));

    // Generate color components with HSL conversion
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.5 + flow1 * 0.7, 1.0), 
        0.6 + flow2 * 0.4,
        0.8 + flow1 * 0.2
    ));

    // Add noise variation to RGB components
    color.r += noise(st * 3.0);
    color.g += noise((st + vec2(1.7)) * 3.0);
    color.b += noise((st + vec2(-1.3)) * 3.0);

    return vec4(color, 1.0);
}
