vec4 Effect2(vec2 uv) {
    // Create base turbulence pattern
    float t1 = turbulent(uv * 0.5 + iTime);
    float t2 = turbulent(uv * 0.7 + vec2(sin(iTime), cos(iTime)));

    // Compute color components using sine functions
    vec3 color = vec3(
        sin(t1 * 4.0 + iTime) * 0.5 + 0.5,
        sin(t2 * 6.0 + iTime * 0.7) * 0.5 + 0.5,
        sin((t1 + t2) * 8.0 + iTime * 1.3) * 0.5 + 0.5
    );

    // Convert to HSL and back to RGB for more vibrant colors
    vec3 hslColor = vec3(
        0.5 + t1 * 0.2,
        0.8,
        0.5 + abs(sin(iTime * 0.3 + t2)) * 0.5
    );

    color = hsl2rgb(hslColor);

    return vec4(color, 0.8);
}
