vec4 Effect2(vec2 uv) {
    // Animate with time
    float time = iTime * 0.5;

    // Create flowing pattern using turbulence
    vec2 st = uv * 10.0 + time;
    float flow = turbulent(st);

    // Add detail with multiple noise layers
    flow += noise(vec3(uv * 5.0, time)) * 0.5;
    flow += noise(vec3(uv * 20.0, time)) * 0.25;

    // Create color shifts using HSL
    vec3 color = hsl(
        mod(time * 0.1 + uv.x * 2.0 + uv.y * 3.0, 1.0),  // Hue variation
        0.7 + flow * 0.3,                               // Saturation modulation
        0.5 + sin(time) * 0.2                           // Value modulation
    );

    // Add lighting effect for water-like appearance
    vec3 lightDir = vec3(1.0, -1.0, 1.0);
    vec3 normal = normalize(vec3(
        noise(vec3(uv + time*0.1, 0.0)) * 2.0 - 1.0,
        noise(vec3(uv + time*0.2, 0.0)) * 2.0 - 1.0,
        1.0
    ));

    float specular = pow(max(dot(normalize(lightDir), normal), 0.0), 32.0);

    // Combine everything for final color
    vec3 finalColor = mix(color, vec3(1.0), specular * 0.5) * (0.5 + flow * 0.5);

    return vec4(finalColor, 1.0);
}
