vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for hexagonal pattern
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + 0.25 * iTime; // Add time-based rotation

    // Create hexagonal pattern using sine function
    float hexPattern = abs(sin(6.0 * theta)) - 0.1;

    // Create distance field for gradient fill
    float distField = (1.0 - r) * 0.5 + 0.5;

    // Add turbulence to create dynamic effect
    vec3 noisePos = vec3(uv * 20.0, iTime);
    float turb = turbulent(noisePos) * 0.5 + 0.5;

    // Combine patterns and add color shifts
    vec3 baseColor = hsl(0.5 + sin(iTime * 0.5) * 0.5, 1.0, 0.5);
    vec3 mixColor = hsl(0.5 + cos(iTime * 0.5) * 0.5, 1.0, 0.5);

    // Final color calculation
    vec3 finalColor = mix(baseColor, mixColor, turb * distField);

    return vec4(finalColor, 1.0);
}
