vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Add time-based rotation and create a swirling effect
    a += iTime * 0.5;
    r = sin(a * 3.0 + iTime) * 0.5 + 0.5;

    // Create expanding rings pattern with turbulence
    float ring = sin(r * 8.0 + iTime) * 0.5 + 0.5;
    float noiseVal = turbulent(vec2(st.x, st.y)) * 0.3;

    // Combine patterns and add color cycling
    vec3 color = hsl2rgb(vec3(
        (iTime * 0.1 + a) * 0.5 + 0.5,
        sin(iTime * 0.3 + r * 4.0) * 0.5 + 0.5,
        ring * (1.0 - noiseVal)
    ));

    return vec4(color, 1.0);
}
