vec4 Effect2(vec2 uv) {
    float t = iTime * 0.5;

    // Create moving sine wave pattern
    vec2 st = uv * 10.0 + vec2(sin(t + uv.x * 3.0), cos(t + uv.y * 3.0));

    // Add noise and turbulence
    float noiseVal = noise(st) * 0.5;
    float turb = turbulent(vec2(st.x * 0.5, st.y * 0.5)) * 0.5;

    // Create RGB shifts
    vec3 color = vec3(
        sin(st.x * 0.5 + t) * 0.5 + 0.5,
        sin(st.y * 0.5 + t * 0.7) * 0.5 + 0.5,
        sin((st.x + st.y) * 0.3 + t * 0.9) * 0.5 + 0.5
    );

    // Apply RGB shifts and noise/turbulence
    color += vec3(
        sin(t * 0.3) * 0.2,
        sin(t * 0.4 + PI) * 0.2,
        sin(t * 0.6 + PI*2.0) * 0.2
    );

    // Combine with noise and turbulence
    color *= (noiseVal + turb) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
