vec4 Effect2(vec2 uv) {
    // Create flowing liquid effect using noise and turbulence
    vec2 st = uv * 5.0 + iTime;

    // Add sine wave movement
    float timeOffset = sin(iTime * 0.5) * 0.5;
    st.x += sin(uv.y * 3.0 + iTime) * 0.5;
    st.y += cos(uv.x * 3.0 + iTime * 0.7) * 0.5;

    // Create turbulence pattern
    float liquid = turbulent(st);

    // Add color transitions using sine waves
    vec3 color = hsl(
        sin(iTime * 0.2 + liquid * 2.0) * 0.5 + 0.5,
        sin(liquid * 4.0 + iTime * 0.1) * 0.5 + 0.7,
        sin(liquid * 3.0 + iTime * 0.3) * 0.5 + 0.5
    );

    // Add depth with multiple sine layers
    float depth = sin(uv.x * 2.0 + iTime) * 0.5;
    depth += sin(uv.y * 2.0 + iTime * 0.7) * 0.3;
    color *= hsl2rgb(vec3(depth, 1.0, 0.8));

    // Add alpha based on noise
    float alpha = noise(st * 0.5);
    return vec4(color, alpha);
}
