vec4 Effect2(vec2 uv) {
    // Animate position over time with cosine waves
    vec2 pos = uv * 8.0 + vec2(cos(iTime), sin(iTime)) * 3.0;

    // Create geometric pattern using mod and noise
    vec2 st = fract(pos);
    float pattern = mod(floor(st.x) + floor(st.y), 2.0);

    // Add time-based color shift
    vec3 hslColor = vec3(
        cos(iTime * 0.5 + pos.x * 0.1) * 0.5 + 0.5,
        0.7,
        0.8
    );

    // Add noise and turbulence for more dynamic effect
    float noiseVal = noise(vec2(pos.x * 0.5, pos.y * 0.5));
    pattern = mix(pattern, noiseVal, 0.3);

    // Convert to RGB and apply geometric overlay
    vec3 color = hsl2rgb(hslColor);
    color *= mix(1.0, 0.5, smoothstep(0.4, 0.6, pattern));

    return vec4(color, 1.0);
}
