vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float r = length(uv);
    float theta = atan2(uv.y, uv.x);
    // Create pulsating effect using time
    float pulse = sin(iTime * 0.5 + r * 10.0) * 0.5 + 0.5;

    // Layered sine waves with increasing frequency and decreasing amplitude
    float wave1 = sin(theta * 3.0 - iTime * 2.0) * 0.4;
    float wave2 = sin(theta * 5.0 - iTime * 3.0) * 0.3;
    float wave3 = sin(theta * 7.0 - iTime * 4.0) * 0.2;

    // Combine waves and add pulsation
    float d = r + pulse + wave1 + wave2 + wave3;

    // Create color gradient using HSL to RGB conversion
    vec3 hslColor = vec3(0.5 + d * 0.2, 0.7, 0.6);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add turbulence for more organic feel
    vec2 noiseUV = uv * 10.0;
    float turb = turbulent(noiseUV) * 0.5 + 0.5;

    // Final color with brightness modulation
    vec3 finalColor = rgbColor * (sin(d * 8.0 - iTime * 2.0) * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
