vec4 Effect2(vec2 uv) {
    // Rotate coordinates over time
    float t = iTime * 0.5;
    vec2 rotatedUv = vec2(
        cos(t)*uv.x - sin(t)*uv.y,
        sin(t)*uv.x + cos(t)*uv.y
    );

    // Create expanding rings pattern
    vec2 center = vec2(0.5, 0.5);
    float dist = length(uv - center);
    float ring = mod(dist * 10.0 + t, 1.0);

    // Create rotating squares pattern
    vec2 squareUv = rotatedUv * 3.0;
    vec2 squarePos = fract(squareUv);
    float square = max(step(0.95, squarePos.x), step(0.95, squarePos.y));

    // Combine patterns with noise
    float noiseVal = turbulent(rotatedUv * 10.0 + t) * 0.5 + 0.5;
    vec3 color = hsl2rgb(vec3(
        mod(t * 0.5, 1.0),
        1.0,
        mix(0.5, noiseVal, 0.7)
    ));

    // Mix patterns and add glow effect
    float glow = max(0.0, 1.0 - length(uv - center) * 2.0);
    vec3 finalColor = mix(color * ring, color * square, 0.5) * (1.0 + glow * 0.5);

    return vec4(finalColor, 1.0);
}
