vec4 Effect2(vec2 uv) {
    vec4 fragColor;

    // Convert UV to polar coordinates for rotation effect
    float theta = atan2(uv.y, uv.x);
    float r = length(uv);

    // Add time-based rotation
    theta += iTime * 3.0; // Rotate at 3 radians per second

    // Create geometric pattern using sine waves and noise
    float pattern = sin(theta * 10.0 + iTime) * 0.5 + 0.5;
    pattern = mod(pattern, PI/2.0); // Quarter-circle pattern

    // Add noise for complexity
    vec3 color = hsl2rgb(vec3(
        fract(iTime * 0.2 + pattern), // Time-based hue variation
        0.7, // Saturation
        0.5 // Lightness
    ));

    // Mix in noise for dynamic transitions
    float noiseValue = noise(uv * 10.0 + iTime);
    color = mix(color, hsl2rgb(vec3(
        fract(iTime * 0.3), 
        0.8,
        0.6
    )), noiseValue * 0.3);

    fragColor = vec4(color, 1.0);
    return fragColor;
}
