vec4 Effect2(vec2 uv) {
    float baseFreq = sin(iTime * 0.5) * 0.5 + 0.5;

    vec3 colorParams = vec3(
        sin(iTime * 0.1 + uv.x * 2.0) * 0.5 + 0.5, // Hue variation
        0.7, // Saturation
        0.8 * baseFreq // Lightness modulation
    );

    float value = 0.0;
    for(int i = 0; i < 4; i++) {
        vec2 st = uv * (vec2(float(i) + 1.0) * 3.0);
        st.x += sin(st.y * 5.0 + iTime) * 0.5;
        st.y += cos(st.x * 5.0 - iTime) * 0.5;

        float layer = sin(st.x * 2.0 + iTime) * 
                     sin(st.y * 2.0 + iTime + PI/2.0);

        layer = abs(layer) * turbulent(uv * (vec2(float(i) + 1.0) * 2.0));

        value += layer * 0.5;
    }

    colorParams.z *= baseFreq * sin(iTime * 0.3 + PI/2.0);
    vec3 color = hsl2rgb(colorParams);

    float brightness = (value * 0.8 + 0.2) * 
                      (baseFreq * 1.5 - 0.5) *
                      sin(iTime * 0.2 + uv.x * PI + uv.y * PI);

    return vec4(color * brightness, 1.0);
}
