vec4 Effect2(vec2 uv) {
    // Create rotating pattern using sine functions
    float time = iTime * 0.5;

    // Transform UV coordinates for rotation effect
    vec2 rotatedUV = vec2(
        uv.x * cos(time) - uv.y * sin(time),
        uv.x * sin(time) + uv.y * cos(time)
    );

    // Add turbulence to create plasma-like effect
    float noise1 = turbulent(rotatedUV * 5.0);
    float noise2 = turbulent(rotatedUV * 10.0 + vec2(sin(time), cos(time)));

    // Combine noise layers with different frequencies
    float combinedNoise = (noise1 + noise2) * 0.5;

    // Create color transitions using HSL to RGB conversion
    vec3 hslColor = vec3(
        sin(time + uv.x * 2.0 + uv.y * 3.0) * 0.5 + 0.5,
        0.7,
        0.8 + combinedNoise * 0.2
    );

    // Convert HSL to RGB and mix with noise patterns
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor = mix(rgbColor, vec3(1.0), abs(combinedNoise));

    return vec4(rgbColor, 1.0);
}
