vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 10.0 + iTime;

    // Create rotating sine wave patterns
    float tx = sin(st.x + cos(st.y + iTime)) * 0.5 + 0.5;
    float ty = cos(st.y + sin(st.x - iTime)) * 0.5 + 0.5;

    // Combine patterns with turbulence
    float pattern = sin(tx * PI + ty * PI) * 0.5 + 0.5;
    pattern += turbulent(vec2(uv * 10.0 + iTime * 0.5)) * 0.3;

    // Create shifting hues using HSL color space
    vec3 color = hsl(pattern * 360.0, 0.7, 0.5);

    return vec4(color, 1.0);
}
