vec4 Effect2(vec2 uv) {
    // Create polar coordinates from UV
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime;  // Add time for rotation

    // Create mirrored patterns using modular arithmetic
    theta = mod(theta * 8.0, PI * 2.0);  // 8-fold symmetry
    float sawtooth = sin(theta * 4.0) * 0.5 + 0.5;  // Sine wave for color shifts

    // Generate geometric pattern using noise and turbulence
    float pattern = turbulent(vec3(r, theta, iTime)) * 0.5 + 0.5;
    pattern *= r * 5.0;  // Scale pattern by radius

    // Create dynamic colors using HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3(0.6 + sawtooth * 0.3, 0.8, 0.7));
    vec3 color2 = hsl2rgb(vec3(0.9 - sawtooth * 0.2, 0.7, 0.5));

    // Mix colors based on pattern
    vec3 finalColor = mix(color1, color2, pattern);

    return vec4(finalColor, 1.0);
}
