//prompt: something wonderful
vec4 Effect2(vec2 uv) {
    vec2 st = uv * 2.0;
    st.x += sin(iTime) * 1.5;
    st.y += cos(iTime * 0.75) * 1.5;

    float t = iTime * 0.3;
    float a = turbulent(st) * 2.0;

    vec3 color = hsl2rgb(vec3(
        (a + sin(a * 4.0)) * 0.25 + t * 0.1,
        0.85,
        0.7
    ));

    return vec4(color, exp(-length(st) * 0.3));
}
