vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create hexagonal pattern using polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Add pulsating effect with cosine waves
    theta += time;
    float petal = cos(theta * 6.0) * cos(time + r * 5.0);

    // Create color transitions using HSL colors
    vec3 colors[3];
    colors[0] = hsl(0.0, 1.0, 0.5);   // Red
    colors[1] = hsl(120.0, 1.0, 0.5); // Green
    colors[2] = hsl(240.0, 1.0, 0.5); // Blue

    // Interpolate between colors based on time and position
    float colorIndex = mod(time * 0.5 + r * 0.2, 3.0);
    vec3 finalColor = mix(colors[int(colorIndex)], 
                         colors[int(colorIndex) % 2 == 0 ? int(colorIndex)+1 : 0], 
                         fract(colorIndex));

    // Add noise for extra texture
    float noiseValue = noise(vec2(uv * 20.0 + time)) * 0.5 + 0.5;
    finalColor *= noiseValue;

    return vec4(finalColor, 1.0);
}
