vec4 Effect2(vec2 uv) {
    // Rotate UV coordinates over time
    float t = iTime * 0.5;
    vec2 rotatedUv = vec2(
        cos(t)*uv.x - sin(t)*uv.y,
        sin(t)*uv.x + cos(t)*uv.y
    );

    // Create multiple layers of sine waves with different frequencies
    float pattern = sin(rotatedUv.x * 10.0 + t) +
                   sin(rotatedUv.y * 20.0 + t*0.5) +
                   sin(length(uv)*30.0 - t);

    // Add turbulence for more organic feel
    vec2 noiseScale = rotatedUv * 4.0;
    float turb = turbulent(noiseScale);

    // Create color cycling effect using HSL
    vec3 hslColor = vec3(
        mod(t*0.1, 1.0),   // Hue cycling over time
        0.7 + turb*0.3,    // Saturation with turbulence variation
        0.5 + pattern*0.2  // Lightness with sine wave pattern
    );

    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor * (1.0 + turb*0.5), 1.0);
}
