vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to create grid cells
    vec2 st = uv * 10.0;

    // Get integer cell indices
    vec2 ij = floor(st);

    // Calculate pulsing factor based on time and position
    float pulse = sin(iTime + (ij.x + ij.y) * 0.5) * 0.5 + 0.5;

    // Alternate colors based on cell coordinates
    vec3 color1 = vec3(1.0, 0.0, 0.0); // Red
    vec3 color2 = vec3(0.0, 0.0, 1.0); // Blue

    // Mix colors based on pulsing effect and cell position
    vec3 finalColor = mix(color1, color2, pulse);

    // Add some noise to create variation
    finalColor += noise(st * 5.0 + iTime) * 0.2;

    return vec4(finalColor, 1.0);
}
