vec4 Effect2(vec2 uv) {
    // Create a grid pattern based on UV coordinates
    vec2 st = fract(uv * 10.0); // Scale up for smaller cells

    // Calculate triangle positions
    vec2 pos = st;
    float d = length(pos - vec2(0.5)); // Distance from center
    float angle = atan2(pos.y - 0.5, pos.x - 0.5);

    // Create tessellation pattern
    float pattern = sin(angle * 3.1415926) * cos(d * 3.1415926 * 2.0);
    pattern = fract(pattern + iTime); // Animate the pattern

    // Generate RGB shift effect
    vec3 color = hsl2rgb(vec3(fract(iTime * 0.2), 1.0, 0.5));

    // Add flowing background with noise
    float flow = turbulent(vec2(uv.x + iTime * 0.1, uv.y + iTime * 0.2)) * 0.5 + 0.5;
    vec3 bgColor = hsl2rgb(vec3(flow * 360.0, 0.7, 0.5));

    // Combine effects
    vec3 finalColor = mix(bgColor, color, pattern);

    return vec4(finalColor, 1.0);
}
