vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Convert to polar coordinates for circular patterns
    float r = length(uv);
    float theta = atan2(uv.y, uv.x);

    // Create pulsating effect using cosine function
    float pulse = cos(r - iTime * 5.0) + 0.5;

    // Add noise to create organic variations
    vec3 noiseFactor = vec3(turbulent(vec3(uv * 10.0, iTime)));
    pulse *= 1.0 + noiseFactor.x * 0.2;

    // Create color fading effect based on distance and time
    float colorFactor = cos(r * 5.0 - iTime) * 0.5 + 0.5;

    // Generate vibrant colors using HSL
    vec3 hslColor = hsl(theta * 180.0 / PI, 0.7, 0.6);

    // Combine all elements to create the final color
    color = hslColor * pulse * colorFactor;

    return vec4(color, 1.0);
}
