vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for radial effects
    vec2 centered = uv - 0.5;
    float radius = length(centered);
    float angle = atan2(centered.y, centered.x);

    // Create pulsating effect using time
    float pulse = sin(iTime * 0.5 + radius * 10.0) * 0.3;
    radius += pulse;

    // Generate color based on angle and radius
    vec3 color = hsl2rgb(vec3(
        (angle + iTime) * 0.5 + 0.5,   // Hue varies with angle and time
        1.0 - radius * 0.8,            // Saturation decreases with distance
        0.5 + sin(radius * 4.0 + iTime) * 0.3  // Brightness pulsates
    ));

    // Add radial motion effect
    vec2 motion = vec2(
        sin(angle - iTime) * radius * 0.1,
        cos(angle + iTime) * radius * 0.1
    );

    // Add turbulence for more dynamic effect
    float turb = turbulent(uv * 4.0 + iTime);
    color *= 1.0 + turb * 0.3;

    return vec4(color, 1.0);
}
