vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Create hexagonal pattern by tiling the angle space
    theta = mod(theta + iTime * 0.5, PI * 2.0);
    float segment = floor(theta / (PI/3.0)) * (PI/3.0);

    // Pulsing effect based on radius and time
    float pulse = sin(iTime * 4.0 + r * 10.0) * 0.5 + 0.5;
    float perturb = noise(vec2(r, iTime)) * 0.3;

    // Create hexagonal pattern with pulsing effect
    float d = abs(theta - segment);
    float c = sin(d * 6.0) * pulse * (1.0 + perturb);

    // Color calculation using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        mod(theta * 5.0 + iTime, 1.0),   // Hue variation
        0.7,                              // Saturation
        0.5 + sin(r * 4.0 - iTime) * 0.5 // Lightness pulsing
    ));

    return vec4(color * c, 1.0);
}
