vec4 Effect2(vec2 uv) {
    float gridSize = 20.0;
    vec2 cellCoords = floor(uv * gridSize);
    vec2 cellCenter = (cellCoords + 0.5) / gridSize;

    // Calculate pulsing intensity using sine waves and time
    float intensity = sin(iTime * 0.5 + cellCoords.x * 0.1) +
                     sin(iTime * 0.7 + cellCoords.y * 0.2);
    intensity = abs(intensity) * 0.5 + 0.5; // Scale to 0-1 range

    // Add turbulence for more dynamic variation
    float turbulentFactor = turbulent(cellCenter * 0.1);
    intensity += turbulentFactor * 0.3;

    // Convert to color using HSL
    vec3 color = hsl(
        iTime * 0.2 + cellCoords.x * 0.1, // Hue variation over time and space
        0.7, // Saturation
        intensity * 0.6 + 0.4 // Lightness based on intensity
    );

    return vec4(color, 1.0);
}
