vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Create rotating pattern using sine waves
    float pattern = sin(theta * 4.0 + iTime) * 0.5 + 0.5;
    pattern += sin(r * 10.0 + theta * 6.0 + iTime) * 0.3;

    // Generate color shifts using sine waves
    vec3 color = vec3(
        sin(theta * 2.0 + iTime * 2.0) * 0.5 + 0.5,
        sin(theta * 3.0 + iTime * 1.5) * 0.5 + 0.5,
        sin(theta * 4.0 + iTime * 1.0) * 0.5 + 0.5
    );

    // Add noise and turbulence for more dynamic effect
    float noiseVal = turbulent(vec3(uv * 10.0, iTime)) * 0.2;
    color *= sin(r * 8.0 + iTime) * 0.5 + 0.5 + noiseVal;

    // Mix colors and create final output
    vec4 finalColor = vec4(color * pattern, 1.0);
    return finalColor;
}
