vec4 Effect2(vec2 uv) {
    vec4 result;

    // Create base frequency with time offset
    float time = iTime * 0.5;

    // Layered sine waves with varying frequencies and directions
    float wave1 = sin(uv.x * 8.0 + time) * 0.5;
    float wave2 = sin(uv.y * 4.0 - time) * 0.3;
    float wave3 = sin((uv.x + uv.y) * 6.0 + time * 2.0) * 0.2;

    // Combine waves with varying frequencies
    float d = wave1 + wave2 + wave3;

    // Add noise and turbulence for more organic feel
    vec2 st = uv * 8.0 + time;
    d += turbulent(st) * 0.1;

    // Create color shifts using HSL
    vec3 color = hsl(d * 0.5 + 0.5, 0.7, 0.6);
    color = hsl2rgb(color);

    // Add depth and glow effect
    result.rgb = mix(vec3(0.1), color, d * 0.5 + 0.5);
    result.a = d * 0.5 + 0.5;

    return result;
}
