vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create rotating hexagonal pattern
    theta -= 0.5 * iTime; // Rotate over time

    // Hexagon tiling
    theta = mod(theta, pi/3.0);
    theta = abs(theta - pi/6.0);

    // Create pulsating color effect
    float hue = (theta + r) / (pi/3.0);
    hue += 0.5 * sin(iTime * 2.0 + theta * 4.0);

    // Add turbulence for dynamic effect
    vec3 noisePos = vec3(uv * 10.0, iTime * 0.5);
    float turb = turbulent(noisePos) * 0.5;

    // Combine color and noise effects
    vec3 color = hsl(hue + turb, 0.7 + 0.3*sin(iTime), 0.5 + 0.5*r);

    // Create hexagonal rings
    float ring1 = step(0.3, r) * (1.0 - step(0.5, r));
    float ring2 = step(0.6, r);

    color *= mix(vec3(1.0), vec3(0.8, 0.9, 1.0), ring1 + ring2);

    return vec4(color, 1.0);
}
