vec4 Effect2(vec2 uv) {
    // Create a time-varying frequency and phase offset for the waves
    float freq = 5.0;
    float speed = 0.3;
    float time = iTime * speed;

    // Generate two sets of sine waves with different frequencies and phases
    vec2 wave1 = sin(uv * freq + time) * 0.5 + 0.5;
    vec2 wave2 = sin((uv + time/4.0) * freq * 0.7 + time*2.0) * 0.5 + 0.5;

    // Combine the waves and add turbulence
    vec2 combined = (wave1 + wave2) * 0.5;
    combined += turbulent(uv * 4.0 + time) * 0.3;

    // Create a color palette using HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3(0.6 + time*0.1, 0.8, 0.7));
    vec3 color2 = hsl2rgb(vec3(0.2 + time*0.15, 0.7, 0.8));

    // Mix the colors based on the wave patterns
    vec3 finalColor = mix(color1, color2, combined.x * 0.6 + 0.4);

    return vec4(finalColor, 1.0);
}
