vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 10.0 + iTime;

    // Create multiple layered sine waves with different frequencies
    float wave1 = sin(st.x * 3.0 + iTime) * 0.5 + 0.5;
    float wave2 = sin(st.y * 4.0 + iTime * 2.0) * 0.5 + 0.5;
    float wave3 = sin((st.x + st.y) * 5.0 + iTime * 3.0) * 0.5 + 0.5;

    // Combine waves with different weights
    float combined = wave1 * 0.4 + wave2 * 0.3 + wave3 * 0.3;

    // Add noise for extra detail
    vec2 noiseUV = uv * 64.0;
    float n = noise(noiseUV) * 0.2;
    combined += n;

    // Create color from combined value and time
    vec3 color = hsl(combined * 0.7 + iTime, 1.0, 0.5);

    return vec4(hsv2rgb(color), 1.0);
}
