vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create orbiting pattern with time-based movement
    a += iTime * 0.5;
    r = abs(r * 3.0 + sin(iTime) * 2.0);

    // Add noise and turbulence for dynamic effect
    vec2 noisePos = uv * 10.0 + vec2(sin(iTime), cos(iTime)) * 5.0;
    float noiseVal = noise(noisePos);
    r += noiseVal * 0.3;
    // Create chromatic aberration effect
    vec3 color;
    color.r = hsl(r + a * 0.5, 0.7, 0.6).r;
    color.g = hsl(r + a * 0.5 + 0.2, 0.7, 0.6).g;
    color.b = hsl(r + a * 0.5 - 0.2, 0.7, 0.6).b;
    // Add dynamic color shifts
    color *= vec3(1.0, 1.0, 1.0) + sin(iTime + vec3(0.0, 2.0, 4.0)) * 0.5;

    return vec4(color, 1.0);
}
