vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates relative to center
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create hexagonal pattern using multiple octaves of sine waves
    float d = 0.0;
    for(float i = 1.0; i <= 4.0; i++) {
        float freq = pow(2.0, i);
        a += sin(a * freq + iTime) * 0.5 / i;
        d += sin(r * freq * 3.0 + a * 6.0) * 0.5 / i;
    }
    // Add time-based rotation
    a += iTime * 0.2;
    // Create pulsating color effect using HSL
    vec3 col = hsl(
        mod(a * 4.0 + iTime, 1.0),   // Hue variation over time and angle
        0.7 + turbulent(vec3(r, a, iTime)) * 0.3,  // Saturation with turbulence
        0.5 + sin(iTime) * 0.5       // Brightness pulsation
    );
    // Convert HSL to RGB and output
    return vec4(hsl2rgb(col), 1.0);
}
