vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);
    float time = iTime * 0.5;

    // Create orbiting motion using sine and cosine functions
    vec2 center = vec2(sin(time * 0.7), cos(time * 0.6)) * 0.4 + uv;

    // Add multiple layers of orbiting polygons
    for(float i = 0.0; i < 3.0; i++) {
        float angle = time * (i + 1.0) + i * PI;
        vec2 pos = center + vec2(sin(angle), cos(angle)) * 0.5;

        // Create pulsating color effect
        vec3 hslColor = vec3(
            fract(time * 0.2 + i * 0.333),
            1.0,
            0.5 + 0.5 * sin(time + angle)
        );
        vec3 rgbColor = hsl2rgb(hslColor);

        // Add noise and turbulence for dynamic effect
        float noiseVal = turbulent(pos * 4.0) * 0.5 + 0.5;
        color += rgbColor * noiseVal;
    }

    // Apply final color transformations
    color *= vec3(1.0, 0.8, 0.6); // Add some warmth to colors

    return vec4(color, 1.0);
}
