vec4 Effect2(vec2 uv) {
    // Animate position based on time
    vec2 st = uv * 5.0 + sin(iTime * 0.3) * 1.5;

    // Create flowing pattern using sine functions
    vec2 flow = sin(st + iTime) * 0.5 + 0.5;

    // Generate base color with dynamic hue
    vec3 color = hsl2rgb(vec3(
        mod(length(flow) * 180.0 + iTime * 60.0, 360.0),
        0.7,
        0.5
    ));

    // Add noise and turbulence for detail
    float noise1 = noise(st + vec2(iTime)) * 0.5;
    float noise2 = turbulent(vec3(st, iTime * 0.2)) * 0.3;

    // Combine elements with smooth transitions
    color += sin(length(flow) * 8.0 + iTime) * 0.4;
    color *= mix(1.0, sin(noise1 * pi), noise2);

    // Add pulsing effect
    float pulse = sin(iTime * 0.5) * 0.3 + 0.7;
    color *= pulse;

    return vec4(color, 1.0);
}
