vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create a rotating grid pattern
    vec2 grid = floor((uv + sin(iTime) * 0.5) * 4.0);
    vec2 cellCenter = (grid + 0.5) / 4.0;

    // Calculate distance from center of nearest square
    float dist = length(uv - cellCenter);

    // Create rotating squares using angle offset
    theta += iTime * 0.5 + sin(grid.x * PI) * 0.5;

    // Use turbulence to add variation
    float turb = turbulent(vec2(theta, r));

    // Create color transitions based on position and time
    vec3 color = hsl2rgb(vec3(
        (theta + iTime) * 0.1,
        1.0,
        0.5 + 0.5 * sin(iTime + theta)
    ));

    // Combine effects to create final output
    return vec4(color, 1.0);
}
