vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Hexagonal grid pattern with pulsation
    float hex = sin(theta * 6.0 + iTime) / r;
    float pulse = sin(iTime * 0.5 + r * 2.0) * 0.5 + 0.5;
    float pattern = (hex * 0.5 + 0.5) * pulse;

    // Color transitions with noise
    vec3 color1 = hsl(r * 0.5 + iTime, 0.7, 0.6);
    vec3 color2 = hsl(theta * 0.5 + iTime, 0.7, 0.4);
    vec3 finalColor = mix(color1, color2, turbulent(vec2(st.x * 0.5, st.y * 0.5)));

    // Motion blur effect
    float motionBlur = turbulent(vec2(uv.x + iTime * 0.1, uv.y));
    finalColor = mix(finalColor, vec3(0.0), motionBlur * 0.5);

    return vec4(finalColor, 1.0);
}
