vec4 Effect2(vec2 uv) {
    vec4 fragColor;

    // Create rotating pattern using time
    float rot = sin(iTime * 0.5);
    vec2 rotatedUV = vec2(
        uv.x * cos(rot) - uv.y * sin(rot),
        uv.x * sin(rot) + uv.y * cos(rot)
    );

    // Add noise and turbulence
    float t = turbulent(rotatedUV * 10.0);

    // Create sine wave pattern with time offset
    float wave = sin(uv.x * 5.0 + iTime) * 0.5 + 0.5;
    vec2 displacedUV = rotatedUV + vec2(0.0, wave * 0.5);

    // Dynamic color transitions using HSL
    float h = 0.5 + t * 0.5;
    float s = 1.0 - abs(sin(iTime)) * 0.5;
    float l = mix(0.3, 0.7, wave);
    vec3 color1 = hsl2rgb(vec3(h, s, l));

    // Secondary color layer
    float h2 = h + 0.5;
    float s2 = s * 0.8;
    float l2 = l * 0.9;
    vec3 color2 = hsl2rgb(vec3(h2, s2, l2));

    // Combine colors based on sine wave pattern
    fragColor = vec4(mix(color1, color2, wave), 1.0);

    return fragColor;
}
