vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);
    float time = iTime * 0.5;

    // Create a grid pattern using noise and turbulence
    vec2 st = uv * 10.0; // Scale up for more cells
    vec2 pos = fract(st); // Get position in current cell

    // Add rotation effect over time
    float angle = sin(time) + cos(time);
    vec2 rotatedPos = vec2(
        pos.x * cos(angle) - pos.y * sin(angle),
        pos.x * sin(angle) + pos.y * cos(angle)
    );

    // Use noise and turbulence for dynamic patterns
    float noiseVal = noise(vec3(rotatedPos, time));
    float turbulentVal = turbulent(vec2(rotatedPos.x * 5.0, rotatedPos.y * 5.0));

    // Create RGB shifts using HSL color space
    vec3 hslColor = vec3(
        fract(time + pos.x) * 360.0,
        1.0,
        0.5
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine patterns and colors
    color += rgbColor * noiseVal;
    color *= turbulentVal;

    return vec4(color, 1.0);
}
