vec4 Effect2(vec2 uv) {
    // Create dynamic sine wave parameters
    float time = iTime * 0.5;

    // Wave 1 parameters
    vec2 w1_pos = uv + vec2(sin(time + uv.x * 2.0) * 0.5, 0.0);
    float w1_amp = 0.3 + turbulent(w1_pos * 4.0 + time) * 0.3;
    float w1_speed = 1.0 + sin(time * 2.0) * 0.5;

    // Wave 2 parameters
    vec2 w2_pos = uv + vec2(0.0, sin(time + uv.y * 2.0) * 0.5);
    float w2_amp = 0.3 + turbulent(w2_pos * 4.0 + time + PI) * 0.3;
    float w2_speed = 1.0 + cos(time * 2.0) * 0.5;

    // Combine waves with noise modulation
    float wave1 = sin((w1_pos.x * 8.0 + time * w1_speed) + uv.y * PI) * w1_amp;
    float wave2 = sin((w2_pos.y * 8.0 + time * w2_speed) + uv.x * PI) * w2_amp;

    // Mix waves with noise
    float mixAmount = noise(vec3(uv * 4.0, time)) * 0.5 + 0.5;
    float finalWave = mix(wave1, wave2, mixAmount);

    // Create color from HSL values
    vec3 color = hsl(
        (uv.x + uv.y + time) * 0.5,
        0.7 + turbulent(uv * 4.0 + time) * 0.3,
        0.5 + finalWave * 0.5
    );

    return vec4(hsl2rgb(color), 1.0);
}
