vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5 + 1.387;

    // Rotating sine wave patterns
    vec2 st = uv - 0.5;
    float angle = time * pi;
    float s = sin(uv.x * 4.0 + angle) * cos(time * 2.0);
    float c = cos(uv.y * 4.0 + angle) * sin(time * 2.0);

    // Dynamic RGB shifts
    vec3 color = hsl2rgb(vec3(
        sin(time * 0.5 + st.x * 2.0) * 0.5 + 0.5,
        cos(time * 0.7 + st.y * 2.0) * 0.5 + 0.5,
        0.8
    ));

    // Add noise and turbulence
    float noiseVal = noise(vec3(uv, time)) * 0.4;
    color += vec3(s * c + noiseVal);

    return vec4(color, 1.0);
}
