vec4 Effect2(vec2 uv) {
    // Create base color with HSL
    vec3 color = hsl2rgb(vec3(0.5 * uv.x + 0.5, 1.0, 0.5));

    // Add time-based wave pattern
    float wave1 = sin(uv.x * 4.0 + iTime) * 0.2;
    float wave2 = sin(uv.y * 4.0 - iTime) * 0.3;
    float wave = (wave1 + wave2) * 0.5;

    // Add noise and turbulence
    vec3 noisePattern = vec3(turbulent(vec3(uv.x * 2.0, uv.y * 2.0, iTime)));
    color += noisePattern * 0.4;

    // Create flowing effect by mixing colors with time
    float hueFlow = (uv.x + uv.y) * 0.5 + iTime * 0.1;
    vec3 finalColor = mix(color, hsl2rgb(vec3(hueFlow - 0.5, 1.0, 0.5)), 0.4);

    return vec4(finalColor, 1.0);
}
