vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Convert to polar coordinates for rotation effect
    float r = length(st);
    float theta = atan2(st.y, st.x);
    theta += iTime * 0.5; // Rotate over time

    // Create rotating grid pattern
    vec2 gridPos = sin(vec2(theta) + iTime) * 10.0;
    float grid = mod(gridPos.x, 1.0) < 0.01 || mod(gridPos.y, 1.0) < 0.01 ? 1.0 : 0.0;

    // Add circular pattern
    float circles = mod(r * 5.0 + iTime, 1.0) < 0.02 ? 1.0 : 0.0;

    // Mix patterns and add noise overlay
    vec3 color = mix(vec3(grid), vec3(circles), 0.5);
    color += hsl2rgb(vec3(theta * 0.5 + iTime * 0.1, 0.7, 0.8));
    color += noise(st * 10.0 + iTime) * 0.2;

    return vec4(color, 1.0);
}
