vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for radial effect
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Add noise and time variation
    float noiseTheta = noise(vec3(theta * 5.0 + iTime, 0.0, 0.0));

    // Create pulsating effect with radius
    float pulse = sin(iTime + r * 20.0) * 0.5 + 0.5;

    // Base color in HSL space
    vec3 hslColor = vec3(
        mod(theta * 3.0 + iTime, 1.0),  // Hue variation with angle and time
        0.8,                              // Saturation
        pulse * 0.7 + 0.3                 // Lightness varying with pulse
    );

    // Add turbulence for more dynamic texture
    float turb = turbulent(vec2(r, theta));
    hslColor.xy += vec2(turb * 0.1, turb * 0.1);

    // Convert to RGB and add noise variation
    vec3 color = hsl2rgb(hslColor) + noise(vec2(r, iTime)) * 0.3;

    // Add fog effect based on radius
    color *= pow(0.5, r * 4.0);

    return vec4(color, 1.0);
}
