vec4 Effect2(vec2 uv) {
    float time = iTime * 0.1;

    // Create rotating sine wave patterns with different frequencies
    vec3 waves = vec3(0.0);

    // Wave 1: slower rotation, lower frequency
    float angle1 = atan2(uv.y - 0.5, uv.x - 0.5) + time * 0.5;
    waves.r += sin(angle1 * 4.0 + time) * 0.3;

    // Wave 2: faster rotation, higher frequency
    float angle2 = atan2(uv.y - 0.5, uv.x - 0.5) + time * 1.0;
    waves.g += sin(angle2 * 8.0 + time * 2.0) * 0.3;

    // Wave 3: medium rotation, varying frequency
    float angle3 = atan2(uv.y - 0.5, uv.x - 0.5) + time * 0.75;
    waves.b += sin(angle3 * 6.0 + time * 1.5) * 0.3;

    // Combine waves and add noise
    float pattern = (waves.r + waves.g + waves.b) * 0.5;
    pattern += noise(uv * 4.0 + time);

    // Create color shifts using HSL
    vec3 color = hsl2rgb(vec3(
        fract(pattern + time) * 360.0,
        0.7,
        0.5 + abs(sin(time)) * 0.5
    ));

    return vec4(color * (1.0 + noise(uv * 8.0 + time * 2.0) * 0.5), 1.0);
}
