vec4 Effect2(vec2 uv) {
    // Center coordinates
    vec2 p = uv - 0.5;

    // Create rotation effect
    float angle = atan(p.y, p.x);
    angle += iTime * 0.5; // Add time-based rotation

    // Calculate distance from center
    float dist = length(p);

    // Create color transitions using HSL
    vec3 color1 = hsl(angle / (2.0 * pi) + 0.5, 0.7, 0.6);
    vec3 color2 = hsl((angle + iTime * 0.1) / (2.0 * pi) + 0.5, 0.7, 0.6);

    // Mix colors based on distance
    vec3 finalColor = mix(color1, color2, dist);

    // Add turbulence effect
    float turb = turbulent(vec3(p * 4.0, iTime * 0.1));
    finalColor *= 0.5 + 0.5 * sin(iTime + turb * 2.0);

    return vec4(finalColor, 1.0);
}
