vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Add time-based rotation
    theta += iTime * 0.2;

    // Create vector pattern using sine waves
    float s1 = sin(theta + iTime) * 0.5 + 0.5;
    float s2 = cos(theta - iTime) * 0.5 + 0.5;

    // Create line patterns
    vec3 color1 = hsl2rgb(vec3(0.0, 1.0, s1));
    vec3 color2 = hsl2rgb(vec3(0.666, 1.0, s2));

    // Mix colors based on position and time
    float mixVal = step(0.5, sin(theta + iTime * 0.5) + 0.5);
    vec3 finalColor = mix(color1, color2, mixVal);

    // Add turbulence effect
    float turb = turbulent(uv * 4.0 + iTime);
    finalColor *= 1.0 + turb * 0.5;

    return vec4(finalColor, 1.0);
}
