vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create spiral pattern with rotation
    a += iTime * 0.5; // Rotation speed
    float spiral = sin(a * 4.0 + r * 3.0) * 0.5 + 0.5;

    // Add noise for cosmic dust effect
    vec2 noiseScale = st * 0.5;
    float noise1 = noise(noiseScale);
    float noise2 = turbulent(vec3(st, iTime * 0.1));

    // Create color gradient based on radius and angle
    vec3 color = hsl(r * 0.6 + 0.3, sin(a * 4.0) * 0.5 + 0.5, 0.8);
    color = mix(color, vec3(1.0), noise2 * 0.5); // Add star effect

    // Combine spiral pattern with noise and color
    float brightness = (spiral + noise1 * 0.5) * 1.5;
    color *= brightness;

    return vec4(hsl2rgb(color), 1.0);
}
