vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Particle count
    const int particles = 16;

    // Time-based animation
    float t = iTime * 0.5;

    for(int i = 0; i < particles; i++) {
        // Random seed for each particle
        vec2 st = uv + vec2(float(i) / float(particles), 0.0);

        // Particle position with noise-based movement
        vec3 pos = vec3(st * 10.0, t);
        pos.xy += noise(pos) * 2.0;
        pos.z += turbulent(pos) * 5.0;

        // Distance from particle to current UV coordinate
        float dist = length(uv - (pos.xy / pos.z));

        // Glow effect based on distance
        float glow = smoothstep(1.0, 0.0, dist);

        // Color with HSL variation
        vec3 hslColor = vec3(t * 0.2 + float(i) / float(particles), 1.0, 0.5);
        vec3 rgbColor = hsl2rgb(hslColor);

        // Add glow to color
        color += rgbColor * glow;
    }

    // Dark background with additive blending
    color *= 0.9;
    color = mix(vec3(0.0), color, 0.8);

    return vec4(color, 1.0);
}
