vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Add time-based rotation
    a += iTime * 0.5;
    // Create hexagonal pattern using polar coordinates
    float hexPattern = abs(sin(a * 3.0 + iTime)) * 0.5 + 0.5;

    // Pulsating color effect
    vec3 color = hsl2rgb(vec3(
        (a / pi + 0.5) * 0.6 + 0.2,  // Hue
        0.7,                          // Saturation
        hexPattern * 0.8 + 0.2        // Lightness
    ));
    // Add dynamic gradient using noise
    float gradient = turbulent(vec3(r * 5.0, a / pi, iTime * 0.1));
    color *= gradient;
    // Create pulsating ring effect
    float ring = abs(sin(r * 4.0 - iTime)) * 0.2;
    color += ring;
    return vec4(color, 1.0);
}
